package simulator;import java.awt.BorderLayout;import java.awt.Container;import javax.swing.JFrame;/* *************************************************************** * Silly Sample program which demonstrates the basic paint * mechanism for Swing components. *************************************************************** */public class Building {    public static void main(String[] args) {        JFrame f = new JFrame("Elevator Simulation");		f.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);				final Controller controller = new Controller();        Container panel = new BuildingPanel(controller);        f.getContentPane().add(panel, BorderLayout.CENTER);        f.pack();        f.setVisible(true);    }	}